class Subject
  def initialize(out=STDOUT)
    cals = RiCal.parse_string <<-END_OF_DATA
BEGIN:VCALENDAR
METHOD:REQUEST
PRODID:Microsoft CDO for Microsoft Exchange
VERSION:2.0
BEGIN:VTIMEZONE
TZID:(GMT-05.00) Eastern Time (US & Canada)
X-MICROSOFT-CDO-TZID:10
BEGIN:STANDARD
DTSTART:16010101T020000
TZOFFSETFROM:-0400
TZOFFSETTO:-0500
RRULE:FREQ=YEARLY;WKST=MO;INTERVAL=1;BYMONTH=11;BYDAY=1SU
END:STANDARD
BEGIN:DAYLIGHT
DTSTART:16010101T020000
TZOFFSETFROM:-0500
TZOFFSETTO:-0400
RRULE:FREQ=YEARLY;WKST=MO;INTERVAL=1;BYMONTH=3;BYDAY=2SU
END:DAYLIGHT
END:VTIMEZONE
BEGIN:VEVENT
DTSTAMP:20090724T143205Z
DTSTART;TZID="(GMT-05.00) Eastern Time (US & Canada)":20090804T120000
SUMMARY:FW: ALL HANDS MEETING
DTEND;TZID="(GMT-05.00) Eastern Time (US & Canada)":20090804T133000
DESCRIPTION:Some event
END:VEVENT
END:VCALENDAR
END_OF_DATA

    cal = cals.first
    @event = cal.events.first
  end
  
  def run
    puts "start"
    @event.dtstart
    puts "done"
  end
end